package array_Basic;

public class Array_simple_print {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a[] = new int[5];
		a[0] = 11;
		a[1] = 22;
		a[2] = 333;
		a[3] = 44;
		a[4] = 55;
		System.out.println(a[1]);
		for(int i=0;i<a.length;i++) {
			System.out.println(a[i]);
		}

	}

}
